    private static void createPhase() {
        new RefPhase(phase: 'Open', roles: 'ROLE_USER, ROLE_AFRL_USER, ROLE_CAO, ROLE_TD_ADMIN, ROLE_FMC_ADMIN', canUserEstimate: true, canCaoEstimate: true, canAddAttendee: true, canDeleteAttendee: true, canEditAttendee: true).save(flush: true)
        new RefPhase(phase: 'Closed').save(flush: true)
        new RefPhase(phase: 'External', roles: 'ROLE_CAO, ROLE_FMC_ADMIN', canCaoEstimate: true, canFmcEstimate: true, canAddAttendee: true, canEditAttendee: true, canManageAttendee: true).save(flush: true)
        new RefPhase(phase: 'Resp TD', roles: 'ROLE_CAO, ROLE_TD_ADMIN, ROLE_FMC_ADMIN', canCaoEstimate: true, canTdEstimate: true, canAddAttendee: true, canEditAttendee: true, canManageAttendee: true).save(flush: true)
        new RefPhase(phase: 'AFRL Review', roles: 'ROLE_FMC_ADMIN', canFmcEstimate: true, canEditAttendee: true, canManageAttendee: true).save(flush: true)
        new RefPhase(phase: 'AFMC Review').save(flush: true)
        new RefPhase(phase: 'SAF Review').save(flush: true)
        new RefPhase(phase: 'Approved', canUserActual: true, canFmcActual: true, canTdActual: true).save(flush: true)
        new RefPhase(phase: 'Finalizing', canUserActual: true, canFmcActual: true, canTdActual: true).save(flush: true)
        new RefPhase(phase: '*ERROR*', roles: 'ROLE_ADMIN, ROLE_DEVELOPER', canUserEstimate: true, canCaoEstimate:true, canAddAttendee: true, canDeleteAttendee: true, canEditAttendee: true).save(flush: true)
    }

